mapper.sql:
join ( select distinct prime.data_source_id, prime.station_id, prime.project_id as activity_projects count(distinct prime.project_id) over (partition by prime.data_source_id, prime.station_id) site_project_count sum(prime.activity_count) over (partition by prime.data_souce_id, prime.station_id) site_activity_count from activity_sum prime
join station_sum quaternary on prime.station_id = quaternary.station_id and prime.data_source_id = quaternary.data_source_id
) tertiary on prime.project_dim_value = tertiary.activity_projects

join ( select distinct prime.data_source_id, prime.station_id, prime.project_id as activity_projects, count(distinct prime.project_id) over (partition by prime.data_source_id, prime.station_id) site_project_count, sum(prime.result_count) over (partition by prime.data_source_id, prime.station_id) site_result_count from result_sum prime
join station_sum quaternary on prime.station_id = quaternary.station_id and prime.data_source_id = quaternary.data_source_id
) tertiary on prime.project_dim_value = tertiary.activity_projects
