mapper.select:
select * from (
select , , , prime.analytical_method, null fcdsc_url, case when detection_limit is not null or detection_limit_unit is not null or detection_limit_desc is not null then #{siteUrlBase,jdbcType=VARCHAR} || '/activities/' || pkg_dynamic_list.url_escape(prime.activity, 'true') || '/results/' || pkg_dynamic_list.url_escape(prime.data_source || '-' || prime.result_id, 'true') || '/resdetectqntlmts' else null end res_detect_qnt_lmt_url, null prep_method_url
, count(distinct prime.activity_id) over (partition by prime.data_source_id, prime.station_id) site_activity_count
, count(*) over (partition by prime.data_source_id, prime.station_id) site_result_count
) prime
and site_activity_count >= #{minactivities[0]}
and site_result_count >= #{minresults[0]}
order by prime.data_source_id, prime.organization, prime.site_id, prime.event_date, prime.activity, prime.result_id
